import React from 'react';

const featuresData = [
  {
    title: 'God Of War',
    description: 'Game ini berfokus pada petualangan Kratos, seorang dewa perang Yunani yang berjuang melawan dewa-dewa lainnya dan monster mitologi.',
    image: 'data:image/jpeg;base64,'
  },
  {
    title: 'GTA V',
    description: 'Permainan ini berlatar di Kota Los Santos dan sekitarnya, kota fiktif yang dibuat berdasarkan kota Los Angeles di Amerika Serikat. Dalam permainan ini, pemain dapat memainkan tiga tokoh utama, Michael De Santa, Trevor Philips, dan Franklin Clinton, yang memiliki tujuan dan misi yang berbeda-beda. Permainan ini menawarkan dunia terbuka yang luas, di mana pemain dapat menjelajahi kota dan pedesaan, serta menjalankan berbagai misi menarik. ',
    image: 'data:image/jpeg;base64,'
  },
  {
    title: 'Assasin Creed',
    description: 'Seri ini berfokus pada petualangan seorang Assassin yang berjuang melawan Templar dalam konflik sejarah yang tersembunyi. Dalam seri ini, pemain dapat mengendalikan karakter yang memiliki kemampuan parkour dan pertempuran yang cerdas untuk mengalahkan musuh dan mencari kebenaran tentang masa lalunya lost in your games with immersive audio and haptic feedback.',
    image: 'data:image/jpeg;base64,'
  },
];

const Features = () => {
  return (
    <section className="features" id="features">
      <h2>Game Favorit</h2>
      <div className="features-grid">
        {featuresData.map((feature, index) => (
          <div key={index} className="feature-item">
            <img src={feature.image} alt={feature.title} />
            <h3>{feature.title}</h3>
            <p>{feature.description}</p>
          </div>
        ))}
      </div>
    </section>
  );
};

export default Features;
